import {withAmp} from 'next/amp'
import React from 'react'
import Head from 'next/head'

const exclusive = (props) => (
    <div>
        <Head>
        <script async custom-element="amp-accordion" src="https://cdn.ampproject.org/v0/amp-accordion-0.1.js"></script>
            <style amp-custom>
                {`

                @media screen and (max-width: 1024px)
                    {
                        .container_exclusive_banner {
                            background-color: #52bdae;
                            display: block;
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                            width:100%;
                            margin: auto;
                        }

                        .right_teal{
                            display:flex;
                        }
                        .left_teal{
                            display:block;
                            padding-top:10%;
                        }
                        .teal_banner_line1{
                            display:block;
                            width:100%;  
                            text-align:center;
                            font-size: 18px;
                            color:white;
                        }
                        .teal_banner_line2{
                            width:100%;
                            text-align:center;
                            color:white;
                            font-size: 18px;
                            display:block;
                        }
                        .teal_banner_line3{
                            font-size:1.7em;
                            width:100%;
                            text-align:center;
                            display:block;
                            font-weight: bold;
                            color:white;
                        }
                        .addtocart_cardone{
                            background-color: white;
                            display:block;
                            margin-left:5%;
                            margin-right:2%;
                            margin-bottom:5%;
                            width:45%;

                        }
                        .addtocart_cardtwo{
                            background-color: white;
                            display:block;
                            margin-right:5%;
                            margin-bottom:5%;
                            width:45%;
                        }
                        .upper_ka_bg{
                            background-color:#e7e6e6;
                            display:inline-block;
                            margin-top:0px;
                            width:100%;

                        }
                        .upper{
                            text-align: center;
                            font-weight: bold;
                            color: #58595f;
                            padding:0;
                            letter-spacing:2%;
                            font-size:15px;
                        }
                        .permonth{
                            color:black;
                            text-align: center;
                            font-size: 12px;
                            color: #58595f;
                            font-weight: bold;
                            letter-spacing: 1px;
                        }
                        .instant_access{
                            color:black;
                            text-align: center;
                            font-size: 12px;
                            color: #58595f;
                            font-weight: bold;
                            letter-spacing: 1px;
                        }
                        .price{
                            background-color:#e7e6e6 ;
                            margin-top:0px;
                            text-align: center;
                            font-weight: bold;
                            margin-bottom:0px;
                            color: #58595f;
                            padding: 2% 10% 2% 5%;
                            letter-spacing: 1px;
                            font-size: 18px;
                        }
                        .addtocart_button{
                            background-color:#ed3833;
                            color: white;
                            padding:8px;
                            display: block;
                            margin:auto;
                            margin-top:10px;
                            margin-bottom:10px;
                            border-radius: 6px;
                        }
                        .brackets_shit{
                            display:block;
                            margin:auto;
                            width:100%;
                            text-align:center;
                            font-size:13px;
                            color:white;

                        }
                        .container_3buttons{
                            display:flex;
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                            margin-top:7%;

                        }
                         .benefits_div{
                            background-color:#e7e6e6;
                            display:flex;
                            padding-left:5%;
                            padding-right:5%;
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                            border-right:2px solid white;
                            width:100%;
                         }
                         .benefits_div:hover{
                             border-bottom:2px solid red;
                         }
                         .benefits{
                            text-align:center;
                            color: #58595f;
                            font-size:10px;
                            display:block;
                            margin:auto;
                            letter-spacing:2px;
                            font-weight:bold;
                         }
                         .price_comp_div{
                            background-color:#e7e6e6;
                            display:flex;
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                            padding-left:5%;
                            padding-right:5%;
                            padding-top:2%;
                            padding-bottom:2%;
                            border-right:2px solid white;

                            width:100%;                            
                         }
                         .price_comp{

                            text-align:center;
                            color: #58595f;
                            font-size:10px;
                            display:block;
                            margin:auto;
                            letter-spacing:2px;
                            font-weight:bold;
                         }
                         .faqs_div{

                            background-color:#e7e6e6;
                            display:flex;
                            padding-left:5%;
                            padding-right:5%;
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                            width:100%;
                         }
                         .faqs{
                            text-align:center;
                            color: #58595f;
                            font-size:10px;
                            display:block;
                            margin:auto;
                            letter-spacing:2px;
                            font-weight:bold;  
                         }
                         .section_title_wrapper{
                             text-align:center;
                             white-space:nowrap;
                             overflow:hidden;
                             display:block;
                             margin-top:5%;

                         }
                         .section_title{
                            position: relative;
                            display: inline-block;
                            font-size: 15px;
                            letter-spacing: 2px;
                            font-weight:bold;
                            color: #ed2d2f;
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                            margin-bottom:5%;
                            z-index:-1;
                         }
                         .section_title:before{
                            right: 100%;
                            margin-right: 10%;
                         }
                         .section_title:after{
                            left: 100%;
                            margin-left: 10%;
                         }
                         .section_title:before, .section_title:after{
                            content: "";
                            position: absolute;
                            top: 50%;
                            width: 9999px;
                            height: 10px;
                            background-image: initial;
                            background-position-x: initial;
                            background-position-y: initial;
                            background-size: initial;
                            background-repeat-x: initial;
                            background-repeat-y: initial;
                            background-attachment: initial;
                            background-origin: initial;
                            background-clip: initial;
                            background-color: rgb(203, 80, 87);
                         }
                         .table_wrapper{
                            display: block;
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                            width:100%;
                            margin-left:7.7%;
                         }
                         table{
                            border-collapse: collapse;
                            border: 5px solid #52bdae;
                            width:85%;
                         }
                         td,th{
                             border:1px solid black;
                             text-align:center;
                             font-size:10px;
                             padding:4%;
                         }
                         .red_prices{
                             color: #ed2d2f
                         }
                         .faqs_footer{
                            font-family: -apple-system, BlinkMacSystemFont, sans-serif;
                         }
                         header{
                             font-size:10px;
                             padding:2%;
                         }
                         .faq_content{
                            font-size:10px;
                            padding:2%;
                            letter-spacing:1px; 
                            color:#5f7082;
                         }
                         div.sticky_soul_red{
                            z-index:2; 
                            display:block;
                            background-color:#ed2d2f;
                            position:fixed;
                            width:100%;
                            height:2.3%;
                            top:0;
                         }
                         div.sticky_soul{
                            z-index:2;
                            posiiton:sticky;
                            top:0;
                            width:100%;
                            margin-top:0.6%;
                         }
                         .nav_soul{

                         }

                    }



                `}

            </style>
        </Head>
        <body>
            <div class="sticky_soul_red">
            </div>
            <div class="sticky_soul">
            </div>
                <center><amp-img class="nav_soul" src="https://images.thesouledstore.com/public/theSoul/storage/tss-logos/exclusive-logo.png" width="80" height="52">
                </amp-img></center>                        
            <div class="container_exclusive_banner">
                <div class="left_teal" >
                    <p>
                        <p class="teal_banner_line1"> BECOME AN EXCLUSIVE MEMBER </p>
                        <p class="teal_banner_line2"> AT AN </p>
                        <p class="teal_banner_line3"> INTRODUCTORY PRICE </p> 
                        <p class="brackets_shit">(Adding membership to cart will unlock Exclusive prices instantly)</p>

                    </p>
                </div>
                <div class="right_teal">
                    <div class="addtocart_cardone">
                        <div class="upper_ka_bg">
                            <p class="upper"> MONTHLY </p>
                            <p class="upper"> MEMBERSHIP </p>
                        </div>    
                            <p class="permonth">Rs. 149/- pm</p>
                            <p class="instant_access">Get instant access 
                                for a full month.</p>
                            <h3 class="price">@ Rs. 149/-</h3>  
                            <button class="addtocart_button">ADD TO CART</button>
                    </div>
                    <div class="addtocart_cardtwo">
                        <div class="upper_ka_bg">
                            <p class="upper"> ANNUAL </p>
                            <p class="upper"> MEMBERSHIP </p>
                        </div>    
                            <p class="permonth">Rs. 25/- pm</p>
                            <p class="instant_access">Get instant access 
                                for a full year.</p>
                            <h3 class="price">@ Rs. 299/-</h3>   
                            <button class="addtocart_button">ADD TO CART</button>
                    </div>
                </div>   
                </div>    
            <div class="container_3buttons">
                    <div class="benefits_div">
                            <p class="benefits"> BENEFITS </p>
                    </div>
                    <div class="price_comp_div">
                            <p class="price_comp"> PRICE COMPARISON </p>
                    </div>
                    <div class="faqs_div">
                            <p class="faqs"> FAQS </p>
                    </div>
            </div>  
            <div class="section_title_wrapper">
                <span class="section_title">BENEFITS</span>
            </div>  
            <div class="section_title_wrapper">
                <span class="section_title">PRICE COMPARISON</span>
            </div>  
            <div class="table_wrapper">
